const {
    mysql
 } = require('mysql');

 const connection = mysql.createConnection({
    host:"localhost",
    user:"root",
    password:"",
    database:"test",
    connectionLimit:"10"
 })

connection.connect((error)=>{
    if(error){
        console.error('Error connection to mysql database: '.error);
        return;
    }
    console.log('Connected');
})